mapper.select:
SELECT UID, EMAIL
FROM USER_T
WHERE EMAIL = #{email}

SELECT UID, EMAIL, UNAME, AUTH, MAJOR, UIMG, CID
FROM USER_T
WHERE UID = #{uid}

mapper.update:
UPDATE USER_T
SET PW = sha1(#{pw}), MAJOR = #{major}, UIMG = #{uimg}, UNAME = #{uname}
WHERE UID = #{uid}

mapper.select:
SELECT S.SID, S.TNAME
FROM MYSTACK_T AS MS JOIN STACK_T AS S ON MS.SID = S.SID
WHERE MS.UID = #{uid}

mapper.update:
UPDATE USER_T
SET AUTH = #{auth}
WHERE UID = #{uid}
